def bubbleSort(arr):    if len(arr) <= 1:        return arr    else:        j = len(arr) - 1        while j > 1:            for i in range(0,j):                if arr[i] < arr[i+1]:                    continue                else:                    exchangeItems(arr,i,i+1)            j -= 1        return arrdef exchangeItems(arr,index1,index2):    temp = arr[index1]    arr[index1] = arr[index2]    arr[index2] = tempt_arr = [9,5,2,1,7,0,-1,5,20,67,4,102,3]print(bubbleSort(t_arr))